                if(pageURL.indexOf("lang") > -1){
                    var lang = getUrlParameter('lang');
                    var needle = 'lang='+lang;
                    var replace = 'lang='+val;
                    var url = pageURL.replace(needle,replace);
                    window.location.href = url;
                } else {
                    window.location.href = pageURL+'?lang='+val;
                }

            } else {
                window.location.reload();
            }
        }
    });
}
</script>
