<script>
var getUrlParameter = function getUrlParameter(sParam) {
    var sPageURL = window.location.search.substring(1),
        sURLVariables = sPageURL.split('&'),
        sParameterName,
        i;

    for (i = 0; i < sURLVariables.length; i++) {
        sParameterName = sURLVariables[i].split('=');

        if (sParameterName[0] === sParam) {
            return sParameterName[1] === undefined ? true : decodeURIComponent(sParameterName[1]);
        }
    }
};
function redirect(val,event){
    var p = $('.cd-popup-lang').children().children()[0];
    $(p).html('This will lead you to GeM '+ val.toUpperCase() + ' website.');
    event.preventDefault();
    $('.cd-popup-lang').addClass('is-visible').show().focus();
    //close popup
    $('.cd-popup-lang').on('click', function(event){
        if( $(event.target).is('.cd-popup-close') || $(event.target).is('.cd-popup-lang') ) {
                event.preventDefault();
                $(this).removeClass('is-visible');
        }
        if($(event.target)[0].innerHTML=='OK'){
            var base_url ="https://gem.gov.in/";
            var pageURL = $(location).attr("href");
            
            if(pageURL.indexOf("lang") > -1){
                var lang = getUrlParameter('lang');
                var needle = 'lang='+lang;
                var replace = 'lang='+val;
                var url = pageURL.replace(needle,replace);
                window.location.href = url;
            } else {
                window.location.href = pageURL+'?lang='+val;
            }
            
        } else {
            window.location.reload();
        }
    });
	//close popup when clicking the esc keyboard button
    $(document).keyup(function(event){
    	if(event.which=='27'){
            $('.cd-popup-lang').removeClass('is-visible');
	}
    });
    $('.cd-popup-lang').keypress(function(event){
        if(event.which == 13){//Enter key pressed
            if( $(event.target).is('.cd-popup-close') || $(event.target).is('.cd-popup-lang') ) {
                event.preventDefault();
                $(this).removeClass('is-visible');
            }
            var a = $(event.target)[0];
            if($(a).find('a').text()=="OK"){
                var base_url ="https://gem.gov.in/";
                var pageURL = $(location).attr("href");
                if(pageURL.indexOf("lang") > -1){
                    var lang = getUrlParameter('lang');
                    var needle = 'lang='+lang;
                    var replace = 'lang='+val;
                    var url = pageURL.replace(needle,replace);
                    window.location.href = url;
                } else {
                    window.location.href = pageURL+'?lang='+val;
                }

            } else {
                window.location.reload();
            }
        }
    });
}
</script>
